package example;

public class exx8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 1;
		
		for (int i = 10; i > 0; i--) {
			System.out.print("카운트다운:" );
			System.out.println(i);
		}
		
		System.out.println("----------");
		
		for (int s = 1; s <= 10; s++) {
			System.out.println(result *= s);
		}
			
	}

}
